            placeholder="0."
            type="number"
            value={branchid}
            onChange={(e) => setBranchID(e.target.value)}
          />
          <Form.Input
            label="Client Address"
            placeholder="0x00..."
            type="text"
            value={clientaddress}
            onChange={(e) => setClientAddress(e.target.value)}
          />
          <Form.Input
            label="Amount"
            placeholder="10..."
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </Form.Group>

        <Button type="submit" onClick={addClient}>
          Submit
        </Button>
