                    <Card.Meta>Amount {data.amountInEur / 10e7} EUR</Card.Meta>
                    <Card.Meta>To Bank {data.toBankId}</Card.Meta>
                    <Card.Meta>To Branch {data.toBranchId}</Card.Meta>
