                </Grid.Column>
                <Grid.Column width={7}>
                  <Header as='h4' inverted>
                    Trust and Security
                  </Header>
                  <p>
                    We Served Our Customer Since The start of the Blockchain Technology.
                  </p>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Container>
        </Segment>
      </div>
    </div>
  );
}
